var expressSession = require('express-session');


/**
 * @class Session store
 * @desc  Express session store
 * @param options
 * @param options.client <Object> exisiting redis client
 * @return RedisStore <Object> new redis store
 */
exports.create = function createSession(options) {
  var RedisStore = require('connect-redis')(expressSession);
  var opts = options || {};
  return new RedisStore(opts);
};
