var debug = require('debug-logger');
var promise = require('./promise');

/**
 * Core utilities
 */

/**
 * @see {@link ./promise.js|promise}
 */
Object.keys(promise).map(function(method) {
  return (exports[method] = promise[method]);
});

/**
 * @function request
 * @see {@link https://www.npmjs.com/package/request-promise|request-promise}
 */
exports.request = require('request-promise');

/**
 * @function debug
 * @see {@link https://www.npmjs.com/package/debug|debug}
 */
exports.debug = debug;
