var test = require('tape');
var configuration = require('../../lib/config');

test('Configuration', {skip: false}, function(t) {
  t.test('read, getProperty, load', function(tt) {
    tt.equal(typeof configuration.read, 'function');
    tt.equal(typeof configuration.getProperty, 'function');
    tt.equal(typeof configuration.load, 'function');
    tt.end();
  });
});
